mapper.select:
SELECT /* getNumberOfRow */
COUNT(*)
FROM
EMP

	
		JOB = #{job}
	
	
		
		AND SAL >= #{salFrom}
		
	
	
		
		AND SAL <= #{salTo}

SELECT /* selectEmpListByCondition */ E.EMPNO, E.ENAME, E.DEPTNO, D.DNAME AS DEPTNAME, E.JOB, E.MGR, E2.ENAME AS MGRNAME, E.HIREDATE, E.COMM, E.SAL FROM EMP E LEFT JOIN DEPT D ON E.DEPTNO = D.DEPTNO, EMP E1 LEFT JOIN EMP E2 ON E1.MGR = E2.EMPNO
E.JOB = #{job}
AND E.SAL >= #{salFrom}
AND E.SAL <= #{salTo}
AND E.EMPNO = E1.EMPNO

SELECT E.EMPNO, E.ENAME, E.DEPTNO, D.DNAME AS DEPTNAME, E.JOB, E.MGR, E2.ENAME AS MGRNAME, E.HIREDATE, E.COMM, E.SAL FROM EMP E LEFT JOIN DEPT D ON E.DEPTNO = D.DEPTNO, EMP E1 LEFT JOIN EMP E2 ON E1.MGR = E2.EMPNO
E.JOB = #{job}
AND E.SAL >= #{salFrom}
AND E.SAL <= #{salTo}
AND E.EMPNO = E1.EMPNO
ORDER BY EMPNO ASC LIMIT #{lowerBound}, #{maxRowPerPage}

SELECT /* selectEmpWithPagination */ EMPNO, ENAME, DEPTNO, DEPTNAME, JOB, MGR, MGRNAME, HIREDATE, COMM, SAL FROM ( SELECT EMPNO, ENAME, DEPTNO, DEPTNAME, JOB, MGR, MGRNAME, HIREDATE, COMM, SAL, ROWNUM AS SUB_ROWNUM FROM ( SELECT E.EMPNO, E.ENAME, E.DEPTNO, D.DNAME AS DEPTNAME, E.JOB, E.MGR, E2.ENAME AS MGRNAME, E.HIREDATE, E.COMM, E.SAL FROM EMP E LEFT JOIN DEPT D ON E.DEPTNO = D.DEPTNO, EMP E1 LEFT JOIN EMP E2 ON E1.MGR = E2.EMPNO
E.JOB = #{job}
AND E.SAL >= #{salFrom}
AND E.SAL <= #{salTo}
AND E.EMPNO = E1.EMPNO
ORDER BY EMPNO ASC ) WHERE ROWNUM < #{upperBound} ) WHERE SUB_ROWNUM > #{lowerBound}

mapper.insert:
INSERT /* insertEmp */ INTO
EMP (
EMPNO, ENAME, DEPTNO, JOB, MGR, HIREDATE, COMM, SAL
) VALUES (
#{empNo}, #{ename}, #{deptNo, jdbcType=NUMERIC}, #{job},
#{mgr,
jdbcType=NUMERIC}, #{hireDate, jdbcType=DATE}, #{comm,
jdbcType=NUMERIC}, #{sal, jdbcType=NUMERIC}
)
